CREATE database customer_behavior;

-- Solving questions for business purpose 
-- 1. What is the total revenue generated by male vs female customers ?
select gender, SUM(purchase_amount) as revenue 
from customer 
group by gender;

-- 2.  which customer used a discount but still spend more than the average purchase amount ? 
select customer_id, purchase_amount from customer where discount_applied = 'Yes' and purchase_amount >= (select AVG(purchase_amount) from customer);

-- 3. which are the top 5 products with the highest avg review rating ? 
select item_purchased, round(avg(review_rating), 2) as 'Average Product Rating' from customer group by item_purchased order by avg(review_rating) desc limit 5;	

-- 4. compare the average purchase amounts between standard and express shipping. 
select shipping_type, round(AVG(purchase_amount), 2) from customer where shipping_type in ('Standard' , 'Express') group by shipping_type;

-- Q5. Do subscribed customers spend more? Compare average spend and total revenue between subscribers and non-subscribers.
select subscription_status, count(customer_id) as total_customers, round(avg(purchase_amount), 2) as avg_spend, round(sum(purchase_amount), 2) as total_revenue from customer group by subscription_status order by total_revenue, avg_spend desc;

-- Q6. Which 5 products have the highest percentage of purchases with discounts applied?
select item_purchased, 
round(sum(case when discount_applied = 'Yes' then 1 else 0 end)/count(*) * 100, 2) as discount_rate from customer group by item_purchased order by discount_rate desc limit 5; 


-- Q7. Segment customers into New, Returning, and Loyal based on their total number of previous purchases, and show the count of each segment. 
with customer_type as (
SELECT customer_id, previous_purchases,
CASE 
    WHEN previous_purchases = 1 THEN 'New'
    WHEN previous_purchases BETWEEN 2 AND 10 THEN 'Returning'
    ELSE 'Loyal'
    END AS customer_segment
FROM customer)

select customer_segment,count(*) AS "Number of Customers" 
from customer_type 
group by customer_segment;

-- Q8. What are the top 3 most purchased products within each category? 
WITH item_counts AS (
    SELECT category,
           item_purchased,
           COUNT(customer_id) AS total_orders,
           ROW_NUMBER() OVER (PARTITION BY category ORDER BY COUNT(customer_id) DESC) AS item_rank
    FROM customer
    GROUP BY category, item_purchased
)
SELECT item_rank,category, item_purchased, total_orders
FROM item_counts
WHERE item_rank <=3;

-- Q9. Are customers who are repeat buyers (more than 5 previous purchases) also likely to subscribe?
SELECT subscription_status,
       COUNT(customer_id) AS repeat_buyers
FROM customer
WHERE previous_purchases > 5
GROUP BY subscription_status;

-- Q10. What is the revenue contribution of each age group? 
SELECT 
    age_groups,
    SUM(purchase_amount) AS total_revenue
FROM customer
GROUP BY age_groups
ORDER BY total_revenue desc;


